/* condição para efetuar login e direcionamento de pagina */
function entrar() {
    /* variaveis de equiparação do valor digitado*/
var adm = "administrador@bifao.com";
var entreg = "entregador@bifao.com";
var client = "cliente@bifao.com";
$(document).ready(function(){
    $('#click').on('click', function(){ /* executa analise quando o botão click for acionado */
        if (adm === $('#email').val()) {/* criterio de avaliação */
            window.open('menuadministrador.html');/* validando o criterio do campo email abre a seguinte pagina*/
        }if (entreg === $('#email').val()) {
            window.open('menuentregador.html');
        }if (client === $('#email').val()){
            window.open('menucliente.html');
        }else{
            window.open('ERRO: LOGIN NÃO REGISTRADO');/* caso não corresponda a nenhum criterio*/
        }
    });
});
}

