function loadMenu(item){
    node = document.getElementById("menu");

    node.innerHTML =
        ((item ==='home')?'Início':'<a href="home.html">Início</a>')+
        " | "
        +((item ==='sobrecarne')?'SobreCarne':'<a href="sobrecarne.html">Sobre a Carne</a>')+
        " | "
        +((item ==='sobreempresa')?'SobreEmpresa':'<a href="sobreempresa.html">Sobre a Empresa</a>')+
        " | "
        +((item === 'contato') ? 'Contato':'<a href="contato.html">Contato</a>')+
        "|"
        +((item === 'entrar') ? 'Entrar':'<a href="entrar.html">Entrar</a>');
}


